document.addEventListener("DOMContentLoaded", () => {
    const expensesList = document.getElementById("expenses-list");

    // Récupérer les dépenses depuis le backend en Go
    fetch("http://localhost:8080/expenses") // Remplace par l'URL de ton API
        .then(response => response.json())
        .then(data => {
            expensesList.innerHTML = "";
            data.forEach(expense => {
                const listItem = document.createElement("li");
                listItem.textContent = `${expense.name}: ${expense.amount} €`;
                expensesList.appendChild(listItem);
            });
        })
        .catch(error => console.error("Erreur lors du chargement des dépenses :", error));
});
